import { NullDataBuilder } from "../src/script/build/null-data-builder";
import { Address } from "../src/bitcoin/address";
import { P2pkhBuilder } from "../src/script/build/p2pkh-builder";
import { P2stasBuilder } from "../src/script/build/p2stas-builder";

describe("testing script building", () => {
  test("build nulldata script", () => {
    const d1 = Buffer.from("tdxp.app");
    const d2 = Buffer.from("l35995@mb,m45,tb,a0.0001");
    const d3 = Buffer.from("text");
    const d4 = Buffer.from("B");
    const scriptBuilder = new NullDataBuilder([d1, d2, d3, d4]);
    const result = scriptBuilder.toHex();

    expect(result).toBe(
      "006a08746478702e617070186c3335393935406d622c6d34352c74622c61302e3030303104746578740142"
    );
  });

  test("build p2pkh script", () => {
    const scriptBuilder = new P2pkhBuilder(
      Address.fromBase58("1AoPwWXXk41vth2J9bHa6wMu65q4j89Q16")
    );
    const result = scriptBuilder.toHex();

    expect(result).toBe("76a9146b7f6a5d5677d1f3635e589b2eacc75d08dc6c4588ac");
  });

  test("build p2stas script", () => {
    const scriptBuilder = new P2stasBuilder(
      Address.fromBase58("1C2dVLqv1kjNn7pztpQ51bpXVEJfoWUNxe"),
      "e3b111de8fec527b41f4189e313638075d96ccd6",
      "MOI"
    );
    const result = scriptBuilder.toHex();

    expect(result).toBe(
      ""
    );
  });
});
